import { React, useState, useEffect } from "react";
import { ArrowLeft, ArrowRight } from "@material-ui/icons";
import "./styles.css";

const GenreCarousel = (props) => {
  const { label, list, handleClick } = props;
  const [scrollLock, setScrollLock] = useState(true);
  const [trailer, setTrailer] = useState("");
  const [click, setClick] = useState(false);
  // getTrailer(movie.id);

  useEffect(() => {
    console.log(click);
    if (click) {
      getTrailer(item.id.substring(19));
      const newElem = document.createElement("iframe");
      newElem.className = "genre-carousel-video";
      newElem.src = trailer;
      let carouselItem = document.querySelectorAll(".genre-carousel-item");
      carouselItem.forEach((item) => {
        item.addEventListener("mouseup", () => {
          return item.contains(newElem)
            ? item.removeChild(newElem)
            : console.log("hi");
        });
        item.addEventListener("mousedown", () => {
          if (item.contains(newElem)) {
            let video = item.querySelector(".genre-carousel-video");
            video.style.transform = "translateY(0)";
          } else {
            item.appendChild(newElem);
          }
        });
      });
    }
    setClick(false);
  }, [click]);

  const getTrailer = async (movieid) => {
    await fetch(`/trailer/${movieid}`)
      .then((response) => response.json())
      .then(({ trailer }) => {
        if (trailer !== "") {
          setTrailer("https://www.youtube.com/embed/" + trailer.key);
        }
      });
  };

  const handleScrollLeft = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(`#${label}-carousel`);
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft -= carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };
  const handleScrollRight = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(`#${label}-carousel`);
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft += carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };

  return (
    <>
      <div className="genre-header">{label}</div>
      <div className="genre-carousel-container">
        <div className="genre-carousel-scroll" onClick={handleScrollLeft}>
          <ArrowLeft class="arrow-scroll-icon" fontSize="large" />
        </div>
        <div
          className="genre-carousel-scroll"
          onClick={handleScrollRight}
          style={{ right: 0 }}
          fontSize="large"
        >
          <ArrowRight class="arrow-scroll-icon" />
        </div>
        <div id={`${label}-carousel`} className="genre-carousel">
          {list.map((movie, index) => {
            return (
              <div
                className="genre-carousel-item"
                id={`genre-carousel-item${movie.id}`}
                onClick={() => {
                  handleClick(movie);
                }}
                onMouseDown={() => {
                  setClick(true);
                }}
              >
                <img src={movie.backdrop_path} alt="" />
                <div className="genre-carousel-item-label">{movie.title}</div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default GenreCarousel;
