    history.push({
      pathname: "/goats",
      state: {
        filter: "goat",
      },
    });
  };
  const handleHomeClick = () => {
    history.push("/");
  };
  const handlePopularClick = () => {
    history.push("/");
  };
  const handleTrendingClick = () => {
    history.push("/");
  };
  const handleLatestClick = () => {
    history.push("/");
  };
  const handleUpcomingClick = () => {
    history.push("/");
  };
  const handleFavouritesClick = () => {
    history.push("/");
  };

  return (
    <div class="drawer-container">
      <div class="drawer-menu-container">
        <div class="drawer-menu-item">
          <img
            src="https://dw-iconusers.flaticon.com/23366/23366610/1628830967584.svg?token=exp=1628831922~hmac=06441946d1616be8108139b8b475e335"
            alt=""
            onClick={handleHomeClick}
          />
        </div>
        <img
          src="https://image.flaticon.com/icons/png/512/1946/1946485.png"
          alt=""
          onClick={() => {}}
        />
        <button onClick={handleGoatsClick}>G.O.A.T's</button>
        <button>Trending</button>
      </div>
    </div>
  );
};

export default Drawer;
