import { React, useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { ArrowLeft, ArrowRight } from "@material-ui/icons";
import "./styles.css";

const HomeOverview = () => {
  const [romanceList, setRomanceList] = useState("");
  const [thrillerList, setThrillerList] = useState("");
  const [cartoonList, setCartoonList] = useState([]);
  const [scrollLock, setScrollLock] = useState(true);

  const history = useHistory();

  useEffect(() => {
    fetch("/genre/16")
      .then((response) => response.json())
      .then(({ results }) => {
        setCartoonList(results);
      });
  }, []);

  const handleClick = (movie) => {
    history.push({
      pathname: `/movie/${movie.original_title}`,
      state: {
        movieObj: movie,
      },
    });
  };

  const handleScrollLeft = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(".genre-carousel");
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.style.transform = `translateX(${carouselItem.clientWidth})`;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 1000);
      return () => {
        clearTimeout(id);
      };
    }
  };
  const handleScrollRight = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(".genre-carousel");
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft += carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 1000);
      return () => {
        clearTimeout(id);
      };
    }
  };

  return (
    <>
      <div className="home-overview-container">
        <div className="genre-header">Cartoons</div>
        <div className="genre-carousel-container">
          <div className="genre-carousel-scroll" onClick={handleScrollLeft}>
            <ArrowLeft class="arrow-scroll-icon" fontSize="large" />
          </div>
          <div
            className="genre-carousel-scroll"
            onClick={handleScrollRight}
            style={{ right: 0 }}
            fontSize="large"
          >
            <ArrowRight class="arrow-scroll-icon" />
          </div>
          <div className="genre-carousel">
            {cartoonList.map((movie, index) => {
              return (
                <div
                  className="genre-carousel-item"
                  key={index}
                  onClick={() => {
                    handleClick(movie);
                  }}
                >
                  <img src={movie.backdrop_path} alt="" />
                  <div className="genre-carousel-item-label">{movie.title}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </>
  );
};

export default HomeOverview;
