import { React } from "react";
import { Header, Drawer } from "../../HomePage";
import { genres } from "../../../assets/movie-api/genres.json";
import "./styles.css";
import { any } from "prop-types";

const MovieView = (props) => {
  const { movieObj } = props.history.location.state;
  return (
    <>
      <Header />
      <Drawer />
      <div class="movie-view-container">
        <div class="movie-view-content">
          <div class="movie-view-main">
            <img class="movie-view-poster" src={movieObj.poster_path} alt="" />
            <div class="movie-view-information">
              <text class="movie-view-title">{movieObj.title}</text>
              <div class="movie-view-rating-genre-container">
                <div class="movie-view-rating-container">
                  <text class="movie-view-rating-header">Score:</text>
                  <text class="movie-view-rating">
                    {movieObj.vote_average} / 10
                  </text>
                </div>
                <div class="movie-view-genre-container">
                  <text class="movie-view-genre-header">Genres:</text>
                  <div>
                    {movieObj.genre_ids.map((genreID, index) => {
                      let genreObj = genres.find((e) => e.id === genreID);
                      if (index === movieObj.genre_ids.length - 1) {
                        return (
                          <text class="movie-view-genre">
                            {genreObj.name},{" "}
                          </text>
                        );
                      } else {
                        return (
                          <text class="movie-view-genre">{genreObj.name}</text>
                        );
                      }
                    })}
                  </div>
                </div>
              </div>
              <text class="movie-view-overview-header">Summary:</text>
              <text class="movie-view-overview">{movieObj.overview}</text>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default MovieView;
