import { React, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { ArrowLeft, ArrowRight } from "@material-ui/icons";
import "./styles.css";
import GenreCarousel from "./GenreCarousel";

const HomeOverview = () => {
  const [romanceList, setRomanceList] = useState("");
  const [thrillerList, setThrillerList] = useState("");
  const [cartoonList, setCartoonList] = useState([]);
  const [scrollLock, setScrollLock] = useState(true);

  const { expand } = useSelector((state) => state.drawer);
  const history = useHistory();
  useEffect(() => {
    fetch("/genre/16")
      .then((response) => response.json())
      .then(({ results }) => {
        setCartoonList(results);
      });
    fetch("/genre/53")
      .then((response) => response.json())
      .then(({ results }) => {
        setThrillerList(results);
      });
    fetch("/genre/10749")
      .then((response) => response.json())
      .then(({ results }) => {
        setRomanceList(results);
      });
  }, []);

  const handleClick = (movie) => {
    history.push({
      pathname: `/movie/${movie.original_title}`,
      state: {
        movieObj: movie,
      },
    });
  };

  const handleScrollLeft = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(".genre-carousel");
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft -= carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };
  const handleScrollRight = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(".genre-carousel");
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft += carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };

  return (
    <>
      <div
        className="home-overview-container"
        style={{
          transform: expand ? "translateX(300px)" : "translateX(-10px)",
        }}
      >
        <GenreCarousel
          handleScrollLeft={handleScrollLeft}
          handleScrollRight={handleScrollRight}
          handleClick={handleClick}
          list={cartoonList}
        />
      </div>
    </>
  );
};

export default HomeOverview;
