import { React, useState } from "react";
import { useHistory } from "react-router-dom";
import { KeyboardArrowRight, KeyboardArrowLeft } from "@material-ui/icons";
import DrawerClosed from "./DrawerClosed";
import DrawerExpanded from "./DrawerExpanded";
import "./styles.css";

const Drawer = () => {
  const history = useHistory();
  const [expand, setExpand] = useState(false);

  const handleDrawerExpand = () => {
    setExpand(!expand);
  };
  const handleHomeClick = () => {
    history.push("/");
  };
  const handlePopularClick = () => {
    history.push({
      pathname: "/goats",
      state: {
        filter: "goat",
      },
    });
  };
  const handleTrendingClick = () => {
    history.push("/");
  };
  const handleLatestClick = () => {
    history.push("/");
  };
  const handleUpcomingClick = () => {
    history.push("/");
  };
  const handleFavouritesClick = () => {
    history.push("/");
  };

  return (
    <>
      {expand ? (
        <div class="drawer-container" style={{ width: 400 }}>
          <DrawerExpanded
            handleHomeClick={handleHomeClick}
            handlePopularClick={handlePopularClick}
            handleTrendingClick={handleTrendingClick}
            handleLatestClick={handleLatestClick}
            handleUpcomingClick={handleUpcomingClick}
            handleFavouritesClick={handleFavouritesClick}
          />
        </div>
      ) : (
        <div class="drawer-container" style={{ width: 70 }}>
          <DrawerClosed
            handleHomeClick={handleHomeClick}
            handlePopularClick={handlePopularClick}
            handleTrendingClick={handleTrendingClick}
            handleLatestClick={handleLatestClick}
            handleUpcomingClick={handleUpcomingClick}
            handleFavouritesClick={handleFavouritesClick}
          />
        </div>
      )}
      <div
        class="expand-drawer-container"
        onClick={handleDrawerExpand}
        style={{ left: expand ? 380 : 20 }}
      >
        {expand ? (
          <KeyboardArrowLeft class="expand-drawer" />
        ) : (
          <KeyboardArrowRight class="expand-drawer" />
        )}
      </div>
    </>
  );
};

export default Drawer;
