import { React, useState, useEffect } from "react";
import { ArrowLeft, ArrowRight } from "@material-ui/icons";
import "./styles.css";

const GenreCarousel = (props) => {
  const { label, list, handleClick } = props;
  const [scrollLock, setScrollLock] = useState(true);
  const [trailer, setTrailer] = useState("");
  const [click, setClick] = useState(false);

  const viewTrailer = () => {};

  const getTrailer = async (movie) => {
    await fetch(`/trailer/${movie.id}`)
      .then((response) => response.json())
      .then(({ trailer }) => {
        if (trailer !== "") {
          // setTrailer(trailer);
          movie.trailer = trailer;
        }
      });
  };

  const handleScrollLeft = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(`#${label}-carousel`);
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft -= carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };
  const handleScrollRight = () => {
    if (scrollLock) {
      setScrollLock(false);
      let carousel = document.querySelector(`#${label}-carousel`);
      let carouselItem = document.querySelector(".genre-carousel-item");
      carousel.scrollLeft += carouselItem.clientWidth;
      let id = setTimeout(() => {
        setScrollLock(true);
      }, 500);
      return () => {
        clearTimeout(id);
      };
    }
  };

  return (
    <>
      <div className="genre-header">{label}</div>
      <div className="genre-carousel-container">
        <div className="genre-carousel-scroll" onClick={handleScrollLeft}>
          <ArrowLeft class="arrow-scroll-icon" fontSize="large" />
        </div>
        <div
          className="genre-carousel-scroll"
          onClick={handleScrollRight}
          style={{ right: 0 }}
          fontSize="large"
        >
          <ArrowRight class="arrow-scroll-icon" />
        </div>
        <div id={`${label}-carousel`} className="genre-carousel">
          {list.map((movie, index) => {
            console.log(movie.trailer);
            if (movie.trailer === undefined) {
              getTrailer(movie);
            }
            return (
              <div
                className="genre-carousel-item"
                id={`genre-carousel-item${movie.id}`}
                onClick={() => {
                  handleClick(movie);
                }}
              >
                {movie.trailer && (
                  <iframe
                    className="genre-carousel-video"
                    src={movie.trailer}
                  />
                )}
                <img src={movie.backdrop_path} alt="" />
                <div className="genre-carousel-item-label">{movie.title}</div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default GenreCarousel;
